weights_tmp_2 = initialize_expansion(expansion_order, eltype(Ts))

# normalization
s_mag = zeros(FastMultipole.length_s(expansion_order))
FastMultipole.update_s_mag!(s_mag, 0, expansion_order)

lamb_helmholtz = Val(true)

FastMultipole.multipole_to_multipole!(translated_weights, translated_branch, branch_weights, branch, weights_tmp_1, weights_tmp_2, Ts, eims, s_mag, Hs_2, expansion_order, lamb_helmholtz)

